pragma solidity >=0.5.0;
// SPDX-License-Identifier: Unlicensed
pragma experimental ABIEncoderV2;

import './open/token/ERC20/IERC20.sol';
import "./open/utils/Initializable.sol";
import "./open/utils/Ownable.sol";

contract CrowdDao is Initializable,Ownable {

    struct User{
        uint invested;          //投资数量
    }

    mapping(address => User) public users;

    struct Crowd{
        uint startT;        //开始时间
        uint endT;          //结束时间
        uint price;         //价格
        uint tokenAmount;   //代币数量
        uint totalTokenAmount;  //总量
        uint surplusTokenAmount;//剩余总量
    }

    Crowd[] public crowds;

    IERC20 public crowdToken;
    IERC20 public purchaseToken;


    event Invest(address indexed owner,uint time,uint payMmount,uint acceptAmount);

    constructor () public {}

     function initialize(
        address _crowdToken,
        address _purchaseToken,
        address _ownerAddress
    ) public initializer {
        crowdToken = IERC20(_crowdToken);
        purchaseToken = IERC20(_purchaseToken);
        _owner = _ownerAddress;
    }


    function getCrowdInfo()external view returns(
            Crowd memory crowd,
            bool isEnd){

        uint len = crowds.length;
        if( len > 0 ){
            crowd = crowds[len -1];
        }
        isEnd = (crowdToken.balanceOf(address(this)) <= 1e18);
    }

    function getUserInfo() external view returns(User memory){
        return users[msg.sender];
    }

    function invest(uint investAmount) external {

        uint len = crowds.length;
        require(len > 0,"not init");

        purchaseToken.transferFrom(msg.sender,address(this),investAmount);

        Crowd storage crowd = crowds[len -1];

        uint time = block.timestamp;
        require( time >= crowd.startT && time <= crowd.endT,"not in time");

        uint acceptToken = investAmount * crowd.tokenAmount / crowd.price;

        crowdToken.transfer(msg.sender,acceptToken);

        User storage user = users[msg.sender];
        user.invested += investAmount;

        crowd.surplusTokenAmount -= acceptToken;

        emit Invest(msg.sender,block.timestamp,investAmount,acceptToken);
    }


    function addRoundCrowd(uint start,uint end,uint price,uint amount,uint total)external onlyOwner{

        crowds.push( Crowd(
            start,end,price,amount,total,total
        ));
    }

    function updateCrowd(uint amount)external onlyOwner{
        uint len = crowds.length;
        require(len > 0,"not init");
        crowds[len - 1].surplusTokenAmount += amount;
        crowds[len - 1].totalTokenAmount += amount;
    }

    function withdraw(uint amount) external onlyOwner{
        uint bal = purchaseToken.balanceOf(address(this));

        if( bal >= amount ){
            purchaseToken.transfer(msg.sender,amount);
        }
    }

    function setNewOwner(address newOwner) external onlyOwner{
        _owner = newOwner;
    }

    function getCrowdLength()external view returns(uint){
        return crowds.length;
    }
}
