// SPDX-License-Identifier: MIT
pragma solidity =0.6.12;
pragma experimental ABIEncoderV2;

import "../open/utils/SafeMath.sol";
import "../open/utils/Ownable.sol";
import "../open/utils/Initializable.sol";
import "../open/token/ERC20/utils/SafeERC20.sol";
import "../open/token/ERC20/ERC20.sol";
import "../open/token/ERC20/IERC20.sol";

interface IPair {
    function totalSupply() external view returns (uint);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function token0() external view returns (address);
}

contract DepositPool is Initializable,Ownable{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    struct UserInfo {
        uint256 amount;     //个人质押数量
        uint256 award;      //已结算收益
        uint256 rewardDebt;     //负债
        uint mined;
        uint minedLimit;
    }

    struct PoolInfo {
        IERC20 lpToken;         //质押代币
        uint256 cakePerBlock;   //每块收益
        uint256 lastRewardBlock;  //上次结算块
        uint256 accCakePerShare;    //累积收益
        uint256 totalHashRate;      //总算力
        bool isBurn;
        uint256 outOutMulriple;
    }

    IERC20 public mineToken;    //收益代币

    address constant internal burnAddress = address(0);

    PoolInfo[] public poolInfo;

    mapping (uint256 => mapping (address => UserInfo)) public userInfo;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);

    constructor() public {}

    function initialize(
        address _mineToken,
        address owner
    ) public initializer {
        mineToken = IERC20(_mineToken);
        _owner = owner;
    }

    function getApy(uint256 _pid)external view returns(uint){
        PoolInfo storage info = poolInfo[_pid];

        if( info.isBurn ){
            return info.outOutMulriple - 100;
        }else{
             uint worth = _getWorth(info,1e18);

            uint totalHashRate = info.totalHashRate;
            if( totalHashRate == 0  ) return 0;

            uint oneYearAward = 1e18 * info.cakePerBlock * 28800 * 365 / totalHashRate;

            return oneYearAward * 100 / worth;
        }
    }

    function _getWorth(PoolInfo storage info,uint amount)internal view returns(uint){

        (uint r0,uint r1,) = IPair(address(info.lpToken)).getReserves();

        uint supply = IPair(address(info.lpToken)).totalSupply();

        require(supply > 0 ,"not init");

        if( address(mineToken) == IPair(address(info.lpToken)).token0()){
            return r0 * amount / supply;
        }else{
            return r1 * amount / supply;
        }
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function add(IERC20 _lpToken, uint256 _cakePerBlock,bool _isBurn,uint _outMulriple) public onlyOwner {
        poolInfo.push(PoolInfo(
            _lpToken,
            _cakePerBlock,
            block.number,
            0,0,_isBurn,_outMulriple
        ));
    }

    function setMineToken(address token)external onlyOwner{
        mineToken = IERC20(token);
    }

    function setCakePerBlock(uint _pid,uint perBlockAward) external onlyOwner{
        poolInfo[_pid].cakePerBlock = perBlockAward;
    }

    function getMultiplier(uint256 _from, uint256 _to) internal pure returns (uint256) {
        return _to.sub(_from);
    }

    function pendingCake(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];

        uint256 accCakePerShare = pool.accCakePerShare;
        uint256 lpSupply = pool.totalHashRate;

        if (block.number > pool.lastRewardBlock && lpSupply != 0) {

            uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);

            uint256 cakeReward = multiplier.mul(pool.cakePerBlock);
            accCakePerShare = accCakePerShare.add(cakeReward.mul(1e12).div(lpSupply));
        }
        uint award = user.amount.mul(accCakePerShare).div(1e12).sub(user.rewardDebt) + user.award;

        if( pool.isBurn && award + user.mined > user.minedLimit ){
            return user.minedLimit - user.mined;
        }
        return award;
    }

    function updatePool(uint256 _pid) internal {
        PoolInfo storage pool = poolInfo[_pid];
        if (block.number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = pool.totalHashRate;
        if (lpSupply == 0) {
            pool.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(pool.lastRewardBlock, block.number);
        uint256 cakeReward = multiplier.mul(pool.cakePerBlock);

        pool.accCakePerShare = pool.accCakePerShare.add(cakeReward.mul(1e12).div(lpSupply));
        pool.lastRewardBlock = block.number;
    }

    function deposit(uint256 _pid, uint256 _amount) public {

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);

        if (user.amount > 0) {
            uint256 pending = user.amount.mul(pool.accCakePerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {

                if( pool.isBurn ){
                    uint limit = getSurplusLimit(user);
                    pending = pending > limit ? limit : pending;
                    if( pending > 0 ){
                        user.award += pending;
                    }
                }else{
                    user.award += pending;
                }
                
            }
        }

        if (_amount > 0) {
            if( pool.isBurn ){
                pool.lpToken.safeTransferFrom(address(msg.sender), address(1), _amount);
                user.minedLimit += _amount * pool.outOutMulriple / 100;
            }else{
                pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            }
            
            user.amount = user.amount.add(_amount);
            pool.totalHashRate += _amount;
            
        }
        user.rewardDebt = user.amount.mul(pool.accCakePerShare).div(1e12);
        emit Deposit(msg.sender, _pid, _amount);
    }

    function withdraw(uint256 _pid, uint256 _amount) public {

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        require(!pool.isBurn ,"not allow");

        require(user.amount >= _amount, "withdraw: not good");

        updatePool(_pid);

        uint256 pending = user.amount.mul(pool.accCakePerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0) {
            user.award += pending;
        }

        if(_amount > 0) {
            user.amount = user.amount.sub(_amount);
            pool.totalHashRate = pool.totalHashRate.sub((_amount));
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }

        user.rewardDebt = user.amount.mul(pool.accCakePerShare).div(1e12);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    function getSurplusLimit(UserInfo storage user)internal view returns(uint){
        uint mined = user.mined + user.award;

        if( user.minedLimit > mined ){
            return user.minedLimit - mined;
        }
        return 0;
    }

    function draw(uint256 _pid)external{

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];

        updatePool(_pid);

        if(user.amount > 0) {

            uint256 pending = user.amount.mul(pool.accCakePerShare).div(1e12).sub(user.rewardDebt);

            if( pending > 0){
                user.award += pending;
            }

            user.rewardDebt = user.amount.mul(pool.accCakePerShare).div(1e12);
        }

        uint v = user.award;

        if( v > 0 ){
            user.award = 0;

            uint amount = v;

            if( pool.isBurn ){
                if( user.mined < user.minedLimit ){

                    if( user.mined + amount > user.minedLimit){
                        amount = user.minedLimit - user.mined;

                        pool.totalHashRate = pool.totalHashRate.sub((user.amount));
                        user.amount = 0;
                        user.mined = 0;
                        user.minedLimit = 0;
                    }else{
                        user.mined += amount;
                    }
                    safeCakeTransfer(msg.sender, amount);
                }
            }else{
                safeCakeTransfer(msg.sender, amount);
            }
        }
    }
    function safeCakeTransfer(address _to, uint256 _amount) internal {
        mineToken.transfer(_to, _amount);
    }
}
