// SPDX-License-Identifier: Unlicensed
pragma solidity >=0.5.0;
pragma experimental ABIEncoderV2;

import '../interfaces/IERC20.sol';
import "../open/utils/Initializable.sol";
import "../open/token/ERC721/IERC721.sol";
import "../open/token/ERC721/utils/ERC721Holder.sol";
import "../open/utils/Ownable.sol";
import '../libraries/SafeMath.sol';
import "../nftLib/FishNftFactory.sol";
import "../nftLib/FishPart.sol";



contract Nmine is ERC721Holder,Initializable,Ownable{

 using SafeMath for uint256;

    IERC721 public nft;                     //nft
    FishNftFactory public factory;

    struct UserInfo {
        uint256 hashRate;                   //
        uint256 award;                      //
        uint256 rewardDebt; 
        uint256 mined;                         //
        uint256[] ids;                         //
        mapping(uint => uint) id2Indexs;    //
    }

    struct PoolInfo {
        uint256 mined;                         //
        uint256 cakePerBlock;               //
        uint256 lastRewardBlock;            //
        uint256 accCakePerShare;            //
        uint256 totalHashRate;              //
    }

    IERC20 public mineToken;                //

    PoolInfo public poolInfo;               //
    mapping (address => UserInfo) internal userInfo;  //

    event Pledge(address indexed owner,uint256 indexed time,uint256 id);
    event UnPledge(address indexed owner,uint256 indexed time,uint256 id);

    constructor() public {}

    function initialize(
        address _mineToken,
        address _nft,
        address _nftFactory,
        uint256 _cakePerBlock,
        address _ownerAddress
    ) public initializer {
         mineToken = IERC20(_mineToken);
         nft = IERC721(_nft);
         factory = FishNftFactory(_nftFactory);
         poolInfo.cakePerBlock = _cakePerBlock;
        _owner = _ownerAddress;
    }

     function getMinerInfo()external view returns(
        uint myPower,
        uint networkPower,
        uint mined,
        uint[] memory ids){

            UserInfo storage miner = userInfo[msg.sender];
            myPower = miner.hashRate;
            networkPower = poolInfo.totalHashRate;
            mined = miner.mined;
            ids = miner.ids;
    }

    function pledge(uint tokenId)external returns(bool){

        nft.safeTransferFrom(msg.sender,address(this),tokenId);

        uint time = block.timestamp;

        UserInfo storage miner = userInfo[msg.sender];

        uint[] storage ids = miner.ids;
        uint len = ids.length;

        if( len == 0 ){
            ids.push(0);
            len += 1;
        }

        ids.push(tokenId);
        miner.id2Indexs[tokenId] = len;

        //FishPart.FishConfig memory config =  factory.getConfig(tokenId);

        deposit(msg.sender,1000);

        emit Pledge(msg.sender,time,tokenId);
        return true;
    }


    function unPledge(uint tokenId)external returns(bool){

        UserInfo storage miner = userInfo[msg.sender];

        uint time = block.timestamp;

        uint[] storage ids = miner.ids;
        uint len = ids.length;

        uint index = miner.id2Indexs[tokenId];
        require(ids[index] > 0,"not owner");
        ids[index] = ids[len -1];
        delete miner.ids[len -1];
        miner.ids.pop();

        if( index != len -1){
            miner.id2Indexs[ids[index]] = index;
        }

        nft.safeTransferFrom(address(this),msg.sender,tokenId);

        //FishPart.FishConfig memory config =  factory.getConfig(tokenId);

        withdraw(msg.sender,1000);

        emit UnPledge(msg.sender,time,tokenId);
    }


    function setCakePerBlock(uint cakePerBlock)external onlyOwner{
        poolInfo.cakePerBlock = cakePerBlock;
    }


    function getMultiplier(uint256 _from, uint256 _to) internal pure returns (uint256) {
        return _to.sub(_from);
    }

    function pendingCake(address owner) external view returns (uint256) {
        UserInfo storage user = userInfo[owner];

        uint256 accCakePerShare = poolInfo.accCakePerShare;
        uint256 lpSupply = poolInfo.totalHashRate;

        if (block.number > poolInfo.lastRewardBlock && lpSupply != 0) {

            uint256 multiplier = getMultiplier(poolInfo.lastRewardBlock, block.number);

            uint256 cakeReward = multiplier.mul(poolInfo.cakePerBlock);
            accCakePerShare = accCakePerShare.add(cakeReward.mul(1e12).div(lpSupply));
        }
        return (user.hashRate.mul(accCakePerShare).div(1e12).sub(user.rewardDebt) + user.award);
    }

    function updatePool() internal {
        if (block.number <= poolInfo.lastRewardBlock) {
            return;
        }
        uint256 lpSupply = poolInfo.totalHashRate;
        if (lpSupply == 0) {
            poolInfo.lastRewardBlock = block.number;
            return;
        }
        uint256 multiplier = getMultiplier(poolInfo.lastRewardBlock, block.number);
        uint256 cakeReward = multiplier.mul(poolInfo.cakePerBlock);

        poolInfo.accCakePerShare = poolInfo.accCakePerShare.add(cakeReward.mul(1e12).div(lpSupply));
        poolInfo.lastRewardBlock = block.number;
    }

    function deposit(address owner, uint256 _amount) internal {

        UserInfo storage user = userInfo[owner];
        updatePool();

        if (user.hashRate > 0) {
            uint256 pending = user.hashRate.mul(poolInfo.accCakePerShare).div(1e12).sub(user.rewardDebt);
            if(pending > 0) {
                user.award += pending;
            }
        }

        if (_amount > 0) {
            user.hashRate = user.hashRate.add(_amount);
            poolInfo.totalHashRate = poolInfo.totalHashRate.add(_amount);
        }
        user.rewardDebt = user.hashRate.mul(poolInfo.accCakePerShare).div(1e12);
    }

    function withdraw(address owner, uint256 _amount) internal {

        UserInfo storage user = userInfo[owner];
        require(user.hashRate >= _amount, "withdraw: not good");

        updatePool();

        uint256 pending = user.hashRate.mul(poolInfo.accCakePerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0) {
            user.award += pending;
        }

        if(_amount > 0) {
            user.hashRate = user.hashRate.sub(_amount);
            poolInfo.totalHashRate = poolInfo.totalHashRate.sub(_amount);
        }

        user.rewardDebt = user.hashRate.mul(poolInfo.accCakePerShare).div(1e12);
    }

    function draw(address owner) external {

        UserInfo storage user = userInfo[owner];

        updatePool();

        uint256 pending = user.hashRate.mul(poolInfo.accCakePerShare).div(1e12).sub(user.rewardDebt);
        if(pending > 0) {
            user.award += pending;
        }

        user.rewardDebt = user.hashRate.mul(poolInfo.accCakePerShare).div(1e12);

        uint award = user.award;

        if( award > 0 ){
            user.award = 0;
            mineToken.transfer(msg.sender,award);
        }
    }
}
