pragma solidity >=0.5.0;
// SPDX-License-Identifier: Unlicensed
pragma experimental ABIEncoderV2;

import './open/token/ERC20/IERC20.sol';
import "./open/utils/Initializable.sol";
import "./open/utils/Ownable.sol";
import "./open/utils/Time.sol";


contract Release is Initializable,Ownable,Time {

    IERC20 public token;

    struct ReleaseInfo{
        uint total;             //释放总量
        uint surplus;           //剩余释放量
        uint period;            //释放周期
        uint periodRelease;     //每周期释放量
        uint lastDrawTime;      //上次结算时间
    }

    struct Network{
        uint total;             //总私募量
        uint released;          //总释放放
        uint userCount;         //总人数
    }

    Network public networkInfo;

    mapping( address => ReleaseInfo[]) userReleaseInfos;

    event ReleaseEvent(address indexed owner,uint time,uint amount );

    constructor () public {}

     function initialize(
        address _token,
        address _ownerAddress
    ) public initializer {
        token = IERC20(_token);
        _owner = _ownerAddress;
    }

    function setToken(address _token) external onlyOwner{
        token = IERC20(_token);
    }

    function addRelease(address owner,uint _total,uint _period,uint _periodNum)external onlyOwner{

        uint len = userReleaseInfos[owner].length;

        require(len < 1,"exceed limit");

        uint _periodRelease = _total / _periodNum;

        userReleaseInfos[owner].push( ReleaseInfo(
            _total,
            _total,
            _period,
            _periodRelease,
            timestempZero()
        ));

        networkInfo.total += _total;

        if( len == 0 ){
            networkInfo.userCount ++;
        }
    }

    function getReleaseInfo()external view returns(ReleaseInfo[] memory){
        return userReleaseInfos[msg.sender];
    }

    function drawRelease()external returns(uint sum){

        ReleaseInfo[] storage infos = userReleaseInfos[msg.sender];

        uint len = infos.length;

        if( len == 0) return 0;

        uint time = timestempZero();

        for( uint i = 0; i < len; i ++ ){
            sum += doDrawRelease(infos[i],time);
        }

        if( sum > 0 ){
            token.transfer(msg.sender, sum);

            networkInfo.released += sum;
            emit ReleaseEvent(msg.sender,block.timestamp,sum);
        }
    }


    function doDrawRelease(ReleaseInfo storage info,uint time)internal returns(uint v){

        uint lastDrawTime = info.lastDrawTime;

        if( time > lastDrawTime ){

            uint num = (time - lastDrawTime) / info.period;

            if( num > 0 ){

                info.lastDrawTime += num * info.period;

                if( info.surplus > 0 ){
                    v = num * info.periodRelease;

                    if( v > info.surplus ) v = info.surplus;

                    info.surplus -= v;
                }
            }
        }
    }
}
